							calEvent.setStart(start);\n

							if( i % 3 == 0 ){\n
								calEvent.setAllDay(true);\n
							}else{\n
								Date d = new Date(start.getTime());\n
								d.setHours(d.getHours() + 1);
								calEvent.setEnd(d);\n
							}\n
							fc.addEvent(calEvent);\n
						}\n
					}\n

